mapper.select:
SELECT id,name,url FROM sys_menu where 1=1 and flag = '0'

    and parent_id = #{parentId,jdbcType=BIGINT}

order by sort asc

SELECT id ,name as 'text' FROM sys_menu where 1=1 and flag = '0'

    and parent_id = #{parentId,jdbcType=BIGINT}

order by sort asc

SELECT

FROM sys_menu where 1=1 and flag = '0'

    and parent_id = #{parentId,jdbcType=BIGINT}


    and name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


    and url = #{url,jdbcType=VARCHAR}


    and perms = #{perms,jdbcType=VARCHAR}


    and sort


    and type = #{type,jdbcType=INTEGER}


    and create_time like CONCAT('%',#{createTime,jdbcType=TIMESTAMP},'%')

SELECT

FROM sys_menu where 1=1

    and parent_id = #{parentId,jdbcType=BIGINT}


    and name = #{name,jdbcType=VARCHAR}


    and url = #{url,jdbcType=VARCHAR}


    and perms = #{perms,jdbcType=VARCHAR}


    and type = #{type,jdbcType=INTEGER}


    and sort = #{sort,jdbcType=INTEGER}

order by create_time desc

mapper.insert:
insert into sys_menu (id, parent_id, name, 
  url, perms, type, sort,flag,create_time
  )
values (#{id,jdbcType=BIGINT}, #{parentId,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, 
  #{url,jdbcType=VARCHAR}, #{perms,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, #{sort,jdbcType=INTEGER},#{flag,jdbcType=TINYINT},
      #{createTime,jdbcType=TIMESTAMP}
  )
